from Crypto.Util.number import bytes_to_long
from secrets import randbits

FLAG = b'greyhats{too_short_for_rsa_3Sae6E}'

p = randbits(1024)
q = randbits(1024)
N = p * q
e = 0x10001

m = bytes_to_long(FLAG)
c = pow(m, e, N)

print(f'c = {c}')
print(f'N = {N}')
print(f'l = {len(FLAG)}')

